&#34;, symname, dlerror()); exit(1); } else { printf(&#34;loaded symbol &#39;%s&#39;&#34; &#34; real addr %p
&#34;, symname, funcptr); } if (funcptr == NULL) { fprintf(stderr, &#34;funcptr is null!
&#34;); abort(); } return funcptr; } static void init_symbols() { if (g_inited == 0) { write(1, &#34;init_symbols()
&#34;, 16); true_open3 = load_sym(&#34;open&#34;); g_inited = 1; } } static int sys_open(const char* path, int flags, mode_t mode) { printf(&#34;sys_open()
&#34;); return openat(AT_FDCWD, path, flags, mode); } int open(const char* path, int flags, ...) { printf(&#34;open3(%s)
&#34;, path); mode_t mode = 0; init_symbols(); if ((flags &amp; O_CREAT) || (flags &amp; O_TMPFILE) == O_TMPFILE) { va_list ap; va_start(ap, flags); mode = va_arg(ap, mode_t); va_end(ap); } return sys_open(path, flags, mode); //return true_open3(path, flags, mode); } build.sh
